mapper.select:
SELECT di.id AS id, oi.id AS orderId, s.id AS statementId, s.customerName AS customerName, di.tc, dis.staffName , dis.staffPhoneNum , CASE WHEN di.state = '1' THEN '출고대기' WHEN di.state = '2' THEN '출고' WHEN di.state = '3' THEN '배송완료' WHEN di.state = '4' THEN '취소' WHEN di.state = '5' THEN '반품' END AS deliveryState, di.expectedDeliveryDt , di.deliveryCompletionDt FROM `deliveryInfo` di INNER JOIN `orderInfo` oi on oi.id = di.orderId INNER JOIN `statement` s on s.id = oi.statementId INNER JOIN `statementProductCord` spc on spc.statementId = oi.statementId INNER JOIN `productCord` pc on spc.productCord = pc.productCord INNER JOIN `productModel` pm on pm.id = pc.productModelId INNER JOIN `deliveryInstallationStaff` dis on dis.id = di.deliveryStaffId WHERE 1=1 AND s.vendorId = #{vendorId}
AND di.id = #{deliveryId}
AND expectedDeliveryDt >= #{expectedDeliveryDt_start} AND expectedDeliveryDt <= #{expectedDeliveryDt_end}
AND pm.modelCord = #{modelCord}
AND dis.staffName LIKE '%${deliveryStaffName}%'
AND customerName LIKE '%${customerName}%'
